/*Table structure for table `cmf_portal_post` */

DROP TABLE IF EXISTS `cmf_portal_post`;

CREATE TABLE `cmf_portal_post` (
  `id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `parent_id` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '父级id',
  `post_type` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '类型,1:文章;2:页面',
  `post_format` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '内容格式;1:html;2:md',
  `user_id` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '发表者用户id',
  `post_status` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '状态;1:已发布;0:未发布;',
  `comment_status` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '评论状态;1:允许;0:不允许',
  `is_top` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否置顶;1:置顶;0:不置顶',
  `recommended` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否推荐;1:推荐;0:不推荐',
  `post_hits` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '查看数',
  `post_like` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '点赞数',
  `comment_count` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT '评论数',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '创建时间',
  `update_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '更新时间',
  `published_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发布时间',
  `delete_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '删除时间',
  `post_title` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '' COMMENT 'post标题',
  `post_keywords` varchar(150) NOT NULL DEFAULT '' COMMENT 'seo keywords',
  `post_excerpt` varchar(500) NOT NULL DEFAULT '' COMMENT 'post摘要',
  `post_source` varchar(150) NOT NULL DEFAULT '' COMMENT '转载文章的来源',
  `post_content` text COMMENT '文章内容',
  `post_content_filtered` text COMMENT '处理过的文章内容',
  `more` text COMMENT '扩展属性,如缩略图;格式为json',
  PRIMARY KEY (`id`),
  KEY `type_status_date` (`post_type`,`post_status`,`create_time`,`id`),
  KEY `parent_id` (`parent_id`),
  KEY `user_id` (`user_id`),
  KEY `create_time` (`create_time`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8mb4 ROW_FORMAT=COMPACT COMMENT='portal应用 文章表';

/*Data for the table `cmf_portal_post` */

insert  into `cmf_portal_post`(`id`,`parent_id`,`post_type`,`post_format`,`user_id`,`post_status`,`comment_status`,`is_top`,`recommended`,`post_hits`,`post_like`,`comment_count`,`create_time`,`update_time`,`published_time`,`delete_time`,`post_title`,`post_keywords`,`post_excerpt`,`post_source`,`post_content`,`post_content_filtered`,`more`) values (1,0,1,1,1,1,1,0,0,9,0,0,1524809570,1524810146,1491250200,0,'2017年全国铁路完成固定资产投资8,010亿元','','','','\n&lt;p style=\'box-sizing: border-box; margin: 20px auto 30px; line-height: 30px; color: rgb(97, 97, 97); font-family: &quot;Source Sans Pro&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\'&gt;中铁总公布,2017年全国铁路完成固定资产投资8,010亿元(人民币,下同),投产新线2,100多公里。截至2017年底,中国铁路营业里程达到12.7万公里,其中高速铁路2.5万公里。 2018年将安排全国铁路固定资产投资7,320亿元,投产新线4,000公里,其中高铁3,500公里，较2017年增加一倍。到2020年,全国铁路营业里程达到15万公里左右,基本覆盖20万人口以上城市,其中高铁3万公里左右,覆盖80%以上的大城市。力争到2025年,铁路网规模达到17.5万公里左右,其中高铁3.8万公里左右。 2018年铁路投资只安排7,320亿元,较2017年减少8.6%,低于市场预期，铁路基建投资则随着整体投资减少及设备投资占比增加而进一步减少。&lt;/p&gt;\n&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;default/20180427/530434df68b244a9739ddcb9511f2663.png&quot; title=&quot;1.png&quot; alt=&quot;1.png&quot;&gt;&lt;/p&gt;\n&lt;p&gt;&lt;br&gt;&lt;/p&gt;\n',NULL,'{\"thumbnail\":\"default\\/20180427\\/530434df68b244a9739ddcb9511f2663.png\",\"template\":\"article\"}'),(2,0,1,1,1,1,1,0,0,2,0,0,1524810257,1524810379,1524810180,0,'李协林董事长捐赠100万元并设立“杨爱芬奖学金”','','“杨爱芬奖学金”是首个在北京交通大学设立面向经管学院学生的永续教育基金，100万元奖励基金全部由北京中立鸿专业服务集团李协林董事长捐赠，并以恩师杨爱芬教授名字命名。“杨爱芬奖学金”为留本基金，每年都会拿出收益中的5万元，对踏实勤奋、品学兼优的学生提供资助。','','\n&lt;p style=\'box-sizing: border-box; margin: 20px auto 30px; line-height: 30px; color: rgb(97, 97, 97); font-family: &quot;Source Sans Pro&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\'&gt;2014年3月28日下午，北京交通大学2012-2013学年“杨爱芬奖学金”发放仪式在交大经济管理学院思源东楼611隆重举行。出席发放仪式的人员包括北京交通大学经济管理学院会计系杨爱芬教授；学校对外联络合作处处长、教育基金会秘书长尹激；学生工作处处长姚念龙；研究生院副院长绳丽慧；对外联络合作处副处长、教育基金会副秘书长徐劲松；研究生工作部挂职副部长闫长丽；经济管理学院党委副书记文映春；经济管理学院党委副书记、副院长崔永梅；经济管理学院研究生科科长宋光森以及经济管理学院团委书记刘景利。获奖学生及学院本科生、研究生代表等50多人一同参加了奖学金发放仪式。北京中立鸿专业服务集团李协林董事长因出差未能参加奖学金发放仪式，特委托交大65级校友、集团总工施文山代表中立鸿集团参加仪式并对获奖学生表示祝贺。&lt;/p&gt;\n&lt;p style=\'box-sizing: border-box; margin: 20px auto 30px; line-height: 30px; color: rgb(97, 97, 97); font-family: &quot;Source Sans Pro&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\'&gt;“杨爱芬奖学金”是首个在北京交通大学设立面向经管学院学生的永续教育基金，100万元奖励基金全部由北京中立鸿专业服务集团李协林董事长捐赠，并以恩师杨爱芬教授名字命名。“杨爱芬奖学金”为留本基金，每年都会拿出收益中的5万元，对踏实勤奋、品学兼优的学生提供资助。“杨爱芬奖学金”旨在加强经管学院优秀人才的培养，本着“公平、公开、公正”的原则，每年经过多伦严格选拔最终确定获奖学生，每次奖励本科生2名、研究生2名、博士生1名。“杨爱芬奖学金”建立三年来资助和奖励了十五名品学兼优的学生，在北京交通大学及广大校友中获得广泛赞誉。&lt;/p&gt;\n&lt;p style=\'box-sizing: border-box; margin: 20px auto 30px; line-height: 30px; color: rgb(97, 97, 97); font-family: &quot;Source Sans Pro&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\'&gt;奖学金发放仪式首先由尹激处长致辞，尹处长向杨爱芬教授和中立鸿集团领导的到来表示衷心的感谢，并向获奖学生表示诚挚的祝贺，希望同学们能够学习李协林董事长，将“饮水思源、爱国荣校”的理念传承不息。随后，杨爱芬教授向获奖的同学表示祝贺，称赞了五位同学在各方面的出色表现，并希望获奖同学用更好的成绩回报母校，回报社会。之后，北京中立鸿专业服务集团总工施文山简要介绍了公司的发展过程以及取得的成果，并表示公司会一如既往的关心和支持经管学院的教学工作。最后，由中立鸿专业服务集团领导和经管学院领导分别为五名获奖的博士、硕士、本科生颁发获奖证书和奖学金。获奖学生代表郑立东同学发言，他代表所有获奖学生向杨爱芬教授、中立鸿专业服务集团以及学校和学院的关心表示真诚的感谢，并代表所有获奖学生郑重承诺：不辜负李协林董事长设立此项奖学金的殷切希望，将用自己更加优异的成绩，回报学校，回报社会。&lt;/p&gt;\n&lt;p&gt;&lt;img src=&quot;default/20180427/e58715eb2cf6cd6fc9b90b404ed6c65a.jpg&quot; style=\'box-sizing: border-box; border: 0px; vertical-align: middle; margin: 10px auto; display: block; max-width: 100%; height: auto; border-radius: 2px; background-clip: padding-box; color: rgb(97, 97, 97); font-family: &quot;Source Sans Pro&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\' title=&quot;2.jpg&quot; alt=&quot;2.jpg&quot;&gt;&lt;/p&gt;\n&lt;p style=\'box-sizing: border-box; margin: 20px auto 30px; line-height: 30px; color: rgb(97, 97, 97); font-family: &quot;Source Sans Pro&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\'&gt;设立“杨爱芬奖学金”，是李协林董事长回馈母校、感恩老师、帮助同学的善举，是中立鸿专业服务集团在社会公益事业中乐于奉献的具体体现。虽然每年获得奖学金的学生数量有限，但李董事长设立奖学金所传递的巨大正能量，为中立鸿集团在学校和学生中间树立了相当的知名度和影响力，同时也带动了一批交大校友踊跃在交大设立类似奖学金。中立鸿集团李协林董事长的善举为北京交通大学在校学生树立了楷模，“回馈母校，奉献社会”的正能量必将随着一批批毕业生走向社会而广为传承。&lt;/p&gt;\n&lt;p&gt;&lt;br&gt;&lt;/p&gt;\n',NULL,'{\"thumbnail\":\"default\\/20180427\\/e58715eb2cf6cd6fc9b90b404ed6c65a.jpg\",\"template\":\"\",\"photos\":[{\"url\":\"default\\/20180427\\/e58715eb2cf6cd6fc9b90b404ed6c65a.jpg\",\"name\":\"2.jpg\"}]}'),(3,0,1,1,1,1,1,0,0,1,0,0,1524810530,1524810530,1524810431,0,'中立鸿公司党群工作总结','','在过去的2013年，在北京注协和公司领导同事们的支持和努力下，党群工作坚持以人为本，努力寻求更好地发挥职能作用，为建设和谐团队、推动事业发展做出了积极努力。现总结如下：','','\n&lt;p style=&quot;box-sizing: border-box;margin: 20px auto 30px;line-height: 30px;color: rgb(97, 97, 97);font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255);text-indent: 47px&quot;&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;在过去的&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;2013&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;年，在北京注协和公司领导同事们的支持和努力下，党群工作坚持以人为本，努力寻求更好地发挥职能作用，为建设和谐团队、推动事业发展做出了积极努力。现总结如下：&lt;/span&gt;&lt;/p&gt;\n&lt;p style=&quot;box-sizing: border-box;margin: 20px auto 30px 85px;line-height: 30px;color: rgb(97, 97, 97);font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;strong style=&quot;box-sizing: border-box&quot;&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;一、&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-variant-numeric: normal;font-variant-east-asian: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal&quot;&gt;      &lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;strong style=&quot;box-sizing: border-box&quot;&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;积极参加学习交流，完善组织机构建设，规范工作制度。&lt;/span&gt;&lt;/strong&gt;&lt;strong style=&quot;box-sizing: border-box&quot;&gt;&lt;/strong&gt;&lt;/p&gt;\n&lt;p style=&quot;box-sizing: border-box;margin: 20px auto 30px;line-height: 30px;color: rgb(97, 97, 97);font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255);text-indent: 47px&quot;&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;由于公司领导的重视和支持，使得公司的党群工作顺利开展。年初，制定了适合本单位的工作学习制度，为有序、高效工作奠定了基础。并及时与北注协及其他兄弟单位协调交流，借鉴学习工作方法和经验。年初党支部认真总结了创先争优活动，为建立长效机制，推进党组织建设建了良好开端。&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;2013&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; mso-hansi-font-family:=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;年组织&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;3&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;人参加入党积极分子培训，预备转正式党员&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;4&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; mso-hansi-font-family:=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;人，&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;3&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;人列为&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;2014&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;年发展对象。组织全体党员深入学习了党的十八大精神，紧密联系实际贯彻落实。&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;2013&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; mso-hansi-font-family:=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;4&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;月，组织单位及员工为四川雅安地震灾区爱心捐款&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;7100&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; mso-hansi-font-family:=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;元。&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;8&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;月份相关人员赴陕西旬邑县革命老区参加了基层党组织书记培训班，扩大了知识视野，&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;促进了党员弘扬优良传统，拓展了思想政治工作新思路。工会工作也开展得有声有色，正式在职职工入会率保持在&lt;span style=&quot;box-sizing: border-box&quot;&gt;98%&lt;/span&gt;以上；依&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;法建立了工会法人，按规定缴纳和使用会费，积极参加北注协举办的各类活动。&lt;/span&gt;&lt;/p&gt;\n&lt;p style=&quot;box-sizing: border-box;margin: 20px auto 30px 85px;line-height: 30px;color: rgb(97, 97, 97);font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;strong style=&quot;box-sizing: border-box&quot;&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;二、&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-variant-numeric: normal;font-variant-east-asian: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal&quot;&gt;      &lt;/span&gt;&lt;/span&gt;&lt;/strong&gt;&lt;strong style=&quot;box-sizing: border-box&quot;&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;发挥职能作用，加强职工队伍建设，为职工办实事。&lt;/span&gt;&lt;/strong&gt;&lt;strong style=&quot;box-sizing: border-box&quot;&gt;&lt;/strong&gt;&lt;/p&gt;\n&lt;p style=&quot;box-sizing: border-box;margin: 20px auto 30px;line-height: 30px;color: rgb(97, 97, 97);font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255);text-indent: 37px&quot;&gt;&lt;strong style=&quot;box-sizing: border-box&quot;&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt; &lt;/span&gt;&lt;/strong&gt;&lt;span times=&quot;&quot; new=&quot;&quot; mso-hansi-font-family:=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;中立鸿党群工作以人为本，关心员工在工作、学习、生活中的诉求和心声，积极上传下达，认真收集会员信息，了解会员最关心最现实的问题，尽力排忧解难，努力建立职工信赖的“家”。主要做法有：&lt;/span&gt;&lt;/p&gt;\n&lt;p style=&quot;box-sizing: border-box;margin: 20px auto 30px 65px;line-height: 30px;color: rgb(97, 97, 97);font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255)&quot;&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;1.&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;利用有限的条件建立了“职工之家活动室&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;”,&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; mso-hansi-font-family:=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;购置了跳绳、球类、棋类等文体用品；&lt;/span&gt;&lt;/p&gt;\n&lt;p style=&quot;box-sizing: border-box;margin: 20px auto 30px 65px;line-height: 30px;color: rgb(97, 97, 97);font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255);text-indent: 9px&quot;&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;2.&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;年初为会员购买公园年票和电影票&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;;&lt;/span&gt;&lt;/p&gt;\n&lt;p style=&quot;box-sizing: border-box;margin: 20px auto 30px;line-height: 30px;color: rgb(97, 97, 97);font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255);text-indent: 75px&quot;&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;3.&lt;/span&gt;&lt;span times=&quot;&quot; new=&quot;&quot; mso-hansi-font-family:=&quot;&quot; style=&quot;box-sizing: border-box;font-size: 19px;font-family: 宋体&quot;&gt;为全体员工办理体检卡&lt;/span&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&gt;;&lt;/span&gt;&lt;/p&gt;\n&lt;p style=&quot;box-sizing: border-box;margin: 20px auto 30px;line-height: 30px;color: rgb(97, 97, 97);font-family: \'Source Sans Pro\', \'Helvetica Neue\', Helvetica, Arial, sans-serif;font-size: 14px;white-space: normal;background-color: rgb(255, 255, 255);text-indent: 75px&quot;&gt;&lt;span style=&quot;box-sizing: border-box;font-size: 19px&quot;&g